import React from 'react';
import Icon1 from "./icons/icon1.png";
import Icon2 from "./icons/icon2.png";
import Icon3 from "./icons/icon3.png";


const PRICING = [
    {
        title: "Go with mobile first",
        description: "Using your fingertip, you can see and manage your entire business. To give seamless service to your customer, Pickmystylist offer your own brand mobile app.",
        icon:Icon1
    },
    {
        title: "Book Online Appointment",
        description: "Let your clients view your real-time availability, schedule their own appointments and make payments anytime, anywhere, on any device.",
        icon:Icon2
    },
    {
        title: "Auto-created customer profiles",
        description: "The customer profile will be instantly created under the appropriate shop once a shop owner has added the appointment.",
        icon: Icon3
    },
];

function App() {

    function renderPlan(name, features, cost, buttonName, icon) {
        return (
            <div className='container mx-auto px-16 py-24 '>
            <div className="grid grid-cols-3 gap-16">
                <img src={icon} alt="" class="pricing-img" />
                <h2 class="pricing-header">{name}</h2>
                <ul class="pricing-features">
                    {features.map((feature) => (
                        <li class="pricing-features-item">{feature}</li>
                    ))}
                </ul>
                <span class="pricing-price">{cost}</span>
                <a href="#/" class="pricing-button">{buttonName}</a>
         </div>
         </div>
        );
    }


    return (
        <div>
        {renderPlan(
            "Personal",
            ["Custom domains", "Sleeps after 30 mins of inactivity"],
            "Free",
            "Sign Up",
            Icon1
        )
        }
        {renderPlan(
                "Small team",
                ["Never sleeps","Multiple workers for more powerful apps"],
                "Free",
                "Sign Up",
                Icon2
        )}
        {renderPlan(
             "Enterprise",
             ["Dedicated","Simple horizontal scalability"],
             "$400",
             "Free trial",
             Icon3
         )
         }
    </div>
    );
}

export default App;
