import React from "react";
import Icon1 from "./icons/icon1.png";
import Icon2 from "./icons/icon2.png";
import Icon3 from "./icons/icon3.png";

function App() {

    function renderPlan(name, features, cost, buttonName, icon) {
        return (
            <div className='container mx-auto px-16 py-2 '>
            <div className="">

                <img src={icon} alt="" />
                <h2>{name}</h2>
                <ul>
                    {features.map((feature) => (
                        <li>{feature}</li>
                    ))}
                </ul>
                <span >{cost}</span>
                <a href="#/" >{buttonName}</a>

            </div>
            </div>
        );
    }

    return (
        <div>
            {renderPlan(
                "Personal",
                ["Custom domains", "Sleeps after 30 mins of inactivity"],
                "Free",
                "Sign Up",
                Icon1
            )
            }
            {renderPlan(
                    "Small team",
                    ["Never sleeps","Multiple workers for more powerful apps"],
                    "Free",
                    "Sign Up",
                    Icon2
            )}
            {renderPlan(
                 "Enterprise",
                 ["Dedicated","Simple horizontal scalability"],
                 "$400",
                 "Free trial",
                 Icon3
             )
             }


        </div>
    );
}

export default App;
